mapper.select:
select p.product_id,p.product_name,p.product_price,p.product_category,p.product_size,p.product_color, p.product_inventory,i.file_name,i.upload_path,i.uuid from product p join product_img i on p.product_id=i.product_id
where (product_name like concat ('%', #{keyword}, '%'))
order by product_id desc limit #{skip}, #{amount}

mapper.update:
update product set product_name=#{productName},
product_price=#{productPrice},product_category=#{productCategory},product_size=#{productSize},product_color=#{productColor},
product_inventory=#{productInventory} where product_id=#{productId};
